import java.io.*; public class Code { public static void main(String[] args) {long startTime = System.currentTimeMillis(); int sum = 0; for (int i = 1; i <= 1000000; i++) { sum += i * (i - 1); } try { Thread.sleep(1000); } catch (InterruptedException e) { e.printStackTrace(); } System.out.println(sum); long endTime = System.currentTimeMillis(); Runtime.getRuntime().gc(); long usedMem = Runtime.getRuntime().totalMemory() - Runtime.getRuntime().freeMemory(); String form = String.format("/Runtime: %f/Used Memory: %d", (endTime - startTime) / 1000.0, usedMem); try (FileWriter writer = new FileWriter("output.txt")) { writer.write(form); } catch (IOException e) { e.printStackTrace(); }} }
